import React, { Component } from "react";

class Champ extends Component {
    render(props) {
        return <div className="mb-3">
            <label htmlFor="nombre" className="form-label">{props.label}</label>
            <input type={props.type} name={props.name} value={props.value} placeholder={props.placeholder} onChange={props.onChange}></input>
        </div>
    }
}
export default Champ;
