import React, { Component, useState } from "react";
import './assets/images/favicon.ico';
import './assets/css/graindashboard.css';
import Champ from "./Champ";
import Link from "../route/route";
import useData from "../route/useData";
import axios from "axios";


const Formulaire = () => {
    // Email et mot de passe +link
    const [email, setEmail] = useState('admin@gmail.com');
    const [password, setPassword] = useState('admin');
    const link = Link.getUseLink();
    const [errormessage, setErrorMessage] = useState("");

    const login = async (event) => {
        event.preventDefault();

        const utilisateur = {
            "email": email,
            "mdp": password
        }
        await axios.post(link + "/signin", utilisateur)
            // const data = useData(link + "signin/" + utilisateur);
            .then(res => {
                if (res.data == 1) {
                    window.location.replace("/recherche");
                }
                else {

                }
                // localStorage.setItem('connected', res.data);
            })
    };

    return (
        <div class="content">
            <div class="container-fluid pb-5">
                <div class="row justify-content-md-center">
                    <div class="card-wrapper col-12 col-md-4 mt-5">
                        <div class="card">
                            <div class="card-body">
                                <h4 class="card-title">Connexion</h4>
                                <form onSubmit={login}>
                                    <div class="form-group">
                                        <input id="email" type="mail" name="email" class="form-control" value={email} placeholder="Votre email" onChange={(event) => setEmail(event.target.value)} ></input>
                                    </div>
                                    <div class="form-group">
                                        <input id="mdp" type="password" name="mdp" class="form-control" value={password} placeholder="Votre mot de passe" onChange={(event) => setPassword(event.target.value)} ></input>
                                    </div>
                                    <div class="form-group">

                                    </div>
                                    <div class="form-group no-margin">
                                        <button type="submit" class="btn btn-primary btn-block">Se connecter</button>
                                    </div>
                                </form>
                            </div>
                        </div>
                        <footer class="footer mt-3">
                            <div class="container-fluid">
                                <div class="footer-content text-center small">
                                    <span class="text-muted">&copy; Project enchere.</span>
                                </div>
                            </div>
                        </footer>
                    </div>
                </div>
            </div >
        </div >
    );
};

export default Formulaire;
