import React, { Component, useEffect, useState } from "react";
import './assets/images/favicon.ico';
import './assets/css/graindashboard.css';
import Header from "./Header";
import Footer from "./Footer";
import Champ from "./Champ";
import { useLink } from "../route/route";
import { useData } from "../route/useData";
function Listeencheres() {
    const [data, setData] = useData('http://localhost:8080/encheres');

    return (
        <ul>
            {data.map(item => (
                <li key={item.idenchere}>{item.nom}</li>
            ))}
        </ul>
    );
}
export default Listeencheres;
