import React, { Component, useState } from "react";
import './assets/images/favicon.ico';
import './assets/css/graindashboard.css';
import Champ from "./Champ";


const Formulaire = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const login = () => {
        const user = {
            "email": email,
            "password": password
        };


    }
    return (
        <div class="content">
            <div class="container-fluid pb-5">
                <div class="row justify-content-md-center">
                    <div class="card-wrapper col-12 col-md-4 mt-5">
                        <div class="card">
                            <div class="card-body">
                                <h4 class="card-title">Connexion</h4>
                                <form onSubmit={login}>
                                    <div class="form-group">
                                        <Champ label="Adresse email" type="mail" name="email" value="admin@gmail.com" placeholder="Votre email" onChange={(event) => setEmail(event.target.value)}></Champ>
                                    </div>
                                    <div class="form-group">
                                        <Champ label="Mot de passe" type="password" name="mdp" value="admin" placeholder="Votre mot de passe" onChange={(event) => setPassword(event.target.value)}></Champ>
                                    </div>
                                    <div class="form-group">

                                    </div>
                                    <div class="form-group no-margin">
                                        <a href="#" class="btn btn-primary btn-block">Se connecter</a>
                                    </div>
                                </form>
                            </div>
                        </div>
                        <footer class="footer mt-3">
                            <div class="container-fluid">
                                <div class="footer-content text-center small">
                                    <span class="text-muted">&copy; Project enchere.</span>
                                </div>
                            </div>
                        </footer>
                    </div>
                </div>
            </div >
        </div >
    );

};
export default Formulaire;
