import React, { Component, useEffect, useState } from "react";
import '../components/assets/images/favicon.ico';
import '../components/assets/css/graindashboard.css';
import { useData } from "../route/useData";
import { useLink } from "../route/route";


export function Tableencheres() {
    const url = useLink();
    const data = useData(url + 'encheres');
    return (

        data.map(item => (
            <tr>
                <td class="py-3"><span class="avatar-placeholder mr-md-2">{item.idutilisateur}</span></td>
                <td class="align-middle py-3">
                    <div class="d-flex align-items-center">
                        <div class="position-relative mr-2">
                            <span class="indicator indicator-lg indicator-bordered-reverse indicator-top-left indicator-success rounded-circle"></span>
                        </div>

                        <a href="">{item.description}</a>
                    </div>
                </td>
                <td class="py-3">{item.nom} {item.prenom}</td>
                <td class="py-3">{item.duree}</td>
                <td class="py-3">
                    <span class="badge badge-pill badge-success">{item.etatenchere}</span>
                </td>
            </tr>
        ))

    );
}

export default Tableencheres;
