import React, { Component, useEffect, useState } from "react";
import './assets/images/favicon.ico';
import '../components/assets//css/graindashboard.css';


function tableencheres() {
    const [data, setData] = useState([]);

    useEffect(() => {
        fetch('http://localhost:8080/encheres')
            .then(response => response.json())
            .then(data => setData(data));
    }, []);

    return (
        data.map(item => (
            <tr>
                <td class="py-3">{item.utilisateur}</td>
                <td class="align-middle py-3">
                    <div class="d-flex align-items-center">
                        <div class="position-relative mr-2">
                            <span class="indicator indicator-lg indicator-bordered-reverse indicator-top-left indicator-success rounded-circle"></span>
                            <span class="avatar-placeholder mr-md-2">{item.idcategorieproduit}</span>
                        </div>

                        <a href="">{item.nom}</a>
                    </div>
                </td>
                <td class="py-3">{item.prenom}</td>
                <td class="py-3">{item.duree}</td>
                <td class="py-3">
                    <span class="badge badge-pill badge-success">{item.datefin}</span>
                </td>
            </tr>
        ))

    );
}

export default tableencheres;
